// CardSelection.js - React Admin configuration for CardSelection resource

// begin MANDATORY imports
import React from 'react';
import { List, Datagrid, TextField, DateField, ReferenceField, Show, SimpleShowLayout,
         TextInput, DateTimeInput, ReferenceInput, SelectInput, SimpleForm, 
         Create, Edit, Filter, Labeled, Button, useRecordContext, useRedirect, Pagination } from 'react-admin';
import AddIcon from '@mui/icons-material/Add';
import { Grid, Box, Typography, Divider } from '@mui/material';
import { required } from 'react-admin';
// end mandatory imports

// Filter for CardSelection List
const CardSelectionFilter = (props) => (
    <Filter {...props}>
        <TextInput label="Search ID" source="id" alwaysOn />
    </Filter>
);

// CardSelection List
export const CardSelectionList = (props) => {
    return (
        <List filters={<CardSelectionFilter />} {...props} sort={{ field: 'id', order: 'ASC' }} pagination={<Pagination rowsPerPageOptions={[5, 10, 25]} showFirstLastButtons />}>
            <Datagrid rowClick="show">
                <TextField source="id" label="ID" />
                <DateField source="selected_date" label="Selected Date" />
                <ReferenceField source="card_id" reference="Card" label="Card">
                    <TextField source="fellowship_name" />
                </ReferenceField>
                <ReferenceField source="circle_type" reference="Circle" label="Circle Type">
                    <TextField source="circle_type" />
                </ReferenceField>
                <ReferenceField source="user_id" reference="User" label="User">
                    <TextField source="name" />
                </ReferenceField>
            </Datagrid>
        </List>
    );
};

// Custom Add Response Button for CardSelection
const AddResponseButton = () => {
    const record = useRecordContext();
    const redirect = useRedirect();

    const handleClick = () => {
        redirect(`/Response/create?source=${encodeURIComponent(JSON.stringify({ card_id: record?.id }))}`);
    };

    return (
        <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={handleClick}
            sx={{ mt: 2 }}
        >
            Add New Response
        </Button>
    );
};

