                        Card Selection Information
                    </Typography>
                    <Grid container spacing={3} sx={{ mb: 2 }}>
                        <Grid item xs={12} sm={6} md={4}>
                            <Box sx={{ p: 1 }}>
                                <Labeled label="ID">
                                    <TextField source="id" />
                                </Labeled>
                            </Box>
                        </Grid>
                        <Grid item xs={12} sm={6} md={4}>
                            <Box sx={{ p: 1 }}>
                                <Labeled label="Selected Date">
                                    <DateField source="selected_date" />
                                </Labeled>
                            </Box>
                        </Grid>
                    </Grid>
                    <Divider sx={{ my: 2 }} />
                </Box>
                <ReferenceField source="card_id" reference="Card" label="Card">
                    <TextField source="fellowship_name" />
                </ReferenceField>
                <ReferenceField source="circle_type" reference="Circle" label="Circle Type">
                    <TextField source="circle_type" />
                </ReferenceField>
                <ReferenceField source="user_id" reference="User" label="User">
                    <TextField source="name" />
                </ReferenceField>
                <AddResponseButton />
            </SimpleShowLayout>
        </Show>
