import React from 'react';
import { Admin, Resource, Loading } from 'react-admin';
import { jsonapiClient } from "./rav4-jsonapi-client/ra-jsonapi-client";
import { createTheme } from '@mui/material/styles';
import { useConf, loadHomeConf } from "./Config";
// end constant imports

// import each resource
import { CustomerList, CustomerShow, CustomerCreate, CustomerEdit } from './Customer';
import { CardList, CardShow, CardCreate, CardEdit } from './Card';
import { CardSelectionList, CardSelectionShow, CardSelectionCreate, CardSelectionEdit } from './CardSelection';
import { CardTagList, CardTagShow, CardTagCreate, CardTagEdit } from './CardTag';
import { CardtypeList, CardtypeShow, CardtypeCreate, CardtypeEdit } from './Cardtype';
import { CircleList, CircleShow, CircleCreate, CircleEdit } from './Circle';
import { DailyReponseCountList, DailyReponseCountShow, DailyReponseCountCreate, DailyReponseCountEdit } from './DailyReponseCount';
import { FellowshipList, FellowshipShow, FellowshipCreate, FellowshipEdit } from './Fellowship';
import { ResponseList, ResponseShow, ResponseCreate, ResponseEdit } from './Response';
import { TagList, TagShow, TagCreate, TagEdit } from './Tag';
import { UserList, UserShow, UserCreate, UserEdit } from './User';

const theme = createTheme({
    palette: {
        primary: { main: '#1976d2' },    // Material-UI default blue
        secondary: { main: '#1565c0' },  // A darker blue, or choose another color
    },
    typography: { fontSize: 14 },
});

const App = () => {
  const [conf, setConf] = React.useState({});
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('loading HomeConf-1');
        const conf = await loadHomeConf();
        setConf(conf);
        setLoading(false);
        console.log('AppConf0: ', conf);
      } catch (error) {
        console.error('Error fetching data:', error);
        sessionStorage.removeItem("raSpa");
      }
    };
    fetchData();
  }, []);

  if (loading) {
    return <Loading loadingPrimary="Loading..." loadingSecondary="Please wait" />;
  }
  const dataProvider = jsonapiClient(conf.api_root, { conf: {} }, null);

  return (
    <Admin dataProvider={dataProvider} theme={theme}>
      <Resource 
        name="Customer" 
        list={CustomerList} 
        show={CustomerShow} 
        edit={CustomerEdit} 
        create={CustomerCreate} 
      />
      <Resource 
        name="Card" 
        list={CardList} 
        show={CardShow} 
        edit={CardEdit} 
        create={CardCreate} 
      />
      <Resource 
        name="CardSelection" 
        list={CardSelectionList} 
        show={CardSelectionShow} 
        edit={CardSelectionEdit} 
        create={CardSelectionCreate} 
      />
      <Resource 
        name="CardTag" 
        list={CardTagList} 
        show={CardTagShow} 
        edit={CardTagEdit} 
        create={CardTagCreate} 
      />
      <Resource 
        name="Cardtype" 
        list={CardtypeList} 
        show={CardtypeShow} 
        edit={CardtypeEdit} 
        create={CardtypeCreate} 
      />
      <Resource 
        name="Circle" 
        list={CircleList} 
        show={CircleShow} 
        edit={CircleEdit} 
        create={CircleCreate} 
      />
      <Resource 
        name="DailyReponseCount" 
        list={DailyReponseCountList} 
        show={DailyReponseCountShow} 
        edit={DailyReponseCountEdit} 
        create={DailyReponseCountCreate} 
      />
      <Resource 
        name="Fellowship" 
        list={FellowshipList} 
        show={FellowshipShow} 
        edit={FellowshipEdit} 
        create={FellowshipCreate} 
      />
      <Resource 
        name="Response" 
        list={ResponseList} 
        show={ResponseShow} 
        edit={ResponseEdit} 
        create={ResponseCreate} 
      />
      <Resource 
        name="Tag" 
        list={TagList} 
        show={TagShow} 
        edit={TagEdit} 
        create={TagCreate} 
      />
      <Resource 
        name="User" 
        list={UserList} 
        show={UserShow} 
        edit={UserEdit} 
        create={UserCreate} 
      />
    </Admin>
  );
};

export default App;
